import * as React from 'react';
import Avatar from '@mui/material/Avatar';
import CssBaseline from '@mui/material/CssBaseline';
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Grid from '@mui/material/Grid';
import Box from '@mui/material/Box';
import IconButton from '@mui/material/IconButton';
import InputAdornment from '@mui/material/InputAdornment';
import LockOutlinedIcon from '@mui/icons-material/LockOutlined';
import VisibilityIcon from '@mui/icons-material/Visibility';
import VisibilityOffIcon from '@mui/icons-material/VisibilityOff';
import Typography from '@mui/material/Typography';
import Container from '@mui/material/Container';
import { createTheme, ThemeProvider } from '@mui/material/styles';
import Button from "@mui/material/Button";
import { GlobalContext } from "./context/global-context";
import AppBar from "@mui/material/AppBar";
import Toolbar from "@mui/material/Toolbar";
import Paper from "@mui/material/Paper";
import Stepper from "@mui/material/Stepper";
import Step from "@mui/material/Step";
import StepLabel from "@mui/material/StepLabel";
import Link from "@mui/material/Link";


function Copyright(props) {
  return (
    <Typography
      variant="body2"
      color="text.secondary"
      align="center"
      {...props}
    >
      {"Copyright © "}
      <Link color="inherit" href="https://www.egcorps.com/">
        EG Corp
      </Link>{" "}
      {new Date().getFullYear()}
      {"."}
    </Typography>
  );
}

const steps = [
  "ข้อมูลส่วนตัว",
  "อาชีพและที่อยู่",
  "ทำเลที่ตั้ง",
  "แจ้งการชำระเงิน",
];

const theme = createTheme();

export default function SignUp({ navigation }) {
  const [state, dispatch] = React.useContext(GlobalContext);
  const [email, setEmail] = React.useState(state.email || "");
  const [password, setPassword] = React.useState(state.password || "");
  const [name, setName] = React.useState(state.name || "");
  const [surname, setSurname] = React.useState(state.surname || "");
  const [tel, setTel] = React.useState(state.tel || "");
  const { next } = navigation;

  const [values, setValues] = React.useState({
    showPassword: false,
  });
  const handleClickShowPassword = () => {
    setValues({
      ...values,
      showPassword: !values.showPassword,
    });
  };


  const handleNext = () => {
    if (email === "" || password === "") return;
    dispatch({
      type: "SAVE_LOGIN_INFO",
      email: email,
      password: password
    });
    // setActiveStep(activeStep + 1);
    next();
  };

  // const handleBack = () => {
  //   setActiveStep(activeStep - 1);
  // };

  return (

    <ThemeProvider theme={theme}><AppBar
    position="absolute"
    color="default"
    elevation={0}
    sx={{
      position: "relative",
      borderBottom: (t) => `1px solid ${t.palette.divider}`,
    }}
  >
    <Toolbar>
      <Typography variant="h6" color="inherit" noWrap>
        LAND PROM
      </Typography>
    </Toolbar>
  </AppBar>

      {/* <React.Fragment>
        {activeStep === steps.length ? (
          <React.Fragment>
            <Typography align="center" gutterBottom>
              <img
                src="images/logoprom.png"
                alt="logo"
                width={50}
                height={50}
              />
            </Typography>

            <Typography variant="h5" gutterBottom>
              ขอบคุณที่สมัครสมาชิกกับเรา
            </Typography>
            <Typography variant="subtitle1">
              ข้อมูลของคุณจะได้รับการตรวจสอบ และทำการลงทะเบียน
              สมาชิกทุกท่านจะได้รับรหัสตัวแทน ฯลฯ
            </Typography>
          </React.Fragment>
        ) : (
          <React.Fragment>
            {getStepContent(activeStep)}
            <Box sx={{ display: "flex", justifyContent: "flex-end" }}>
              {activeStep !== 0 && (
                <Button onClick={handleBack} sx={{ mt: 3, ml: 1 }}>
                  ย้อนกลับ
                </Button>
              )}

              <Button
                variant="contained"
                onClick={handleNext}
                sx={{ mt: 3, ml: 1 }}
              >
                {activeStep === steps.length - 1 ? "สมัครสมาชิก" : "ต่อไป"}
              </Button>
            </Box>
          </React.Fragment>
        )}
      </React.Fragment> */}

  <Container component="main" maxWidth="sm" sx={{ mb: 4 }}>
  <Paper
    variant="outlined"
    sx={{ my: { xs: 3, md: 6 }, p: { xs: 2, md: 3 } }}
  >
    <Typography component="h1" variant="h4" align="center">
      ลงทะเบียน LP 4/2
    </Typography>
    <Stepper activeStep={next()} sx={{ pt: 3, pb: 5 }}>
      {steps.map((label) => (
        <Step key={label}>
          <StepLabel>{label}</StepLabel>
        </Step>
      ))}
    </Stepper>
        <CssBaseline />
        <Box
          sx={{
            marginTop: 8,
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
          }}
        >
          <Avatar sx={{ m: 1, bgcolor: 'primary.main' }}>
            <LockOutlinedIcon />
          </Avatar>
          <Typography component="h1" variant="h5">
            ข้อมูลส่วนตัว
          </Typography>
          {/* <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 3 }}> */}
          <Box>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  autoComplete="given-name"
                  name="firstName"
                  required
                  fullWidth
                  id="firstName"
                  label="ชื่อ"
                  defaultValue={name}
                  onChange={(e) => setName(e.target.value)}
                  autoFocus
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  id="lastName"
                  label="นามสกุล"
                  name="lastName"
                  defaultValue={surname}
                  onChange={(e) => setSurname(e.target.value)}
                  autoComplete="family-name"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="tel"
                  label="เบอร์โทรศัพท์"
                  name="tel"
                  defaultValue={tel}
                  onChange={(e) => setTel(e.target.value)}
                  autoComplete="tel"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  defaultValue={email}
                  onChange={(e) => setEmail(e.target.value)}
                  name="email"
                  autoComplete="email"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  defaultValue={password}
                  onChange={(e) => setPassword(e.target.value)}
                  type={values.showPassword ? 'text' : 'password'}
                  id="password"
                  autoComplete="new-password"
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          edge="end"
                          aria-label="Toggle password visibility"
                          onClick={handleClickShowPassword}
                        >
                          {values.showPassword ? <VisibilityOffIcon /> : <VisibilityIcon />}
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <FormControlLabel
                  control={<Checkbox value="allowExtraEmails" color="primary" />}
                  label="ฉันยินยอมรับข้อมูลข่าวสาร ข้อมูลทางการตลาด ต่างๆ จากทางบริษัท"
                />
              </Grid>
            </Grid>
          </Box>
        </Box>
        {/* <Copyright sx={{ mt: 5 }} /> */}
        <React.Fragment>
                <Box sx={{ display: "flex", justifyContent: "flex-end" }}>
                    {/* <Button onClick={handleBack} sx={{ mt: 3, ml: 1 }}>
                      ย้อนกลับ
                    </Button> */}
                  <Button
                    variant="contained"
                    onClick={handleNext}
                    sx={{ mt: 3, ml: 1 }}
                  >
                   ต่อไป
                  </Button>
                </Box>

          </React.Fragment>
          </Paper>
    <Copyright />
  </Container>
    </ThemeProvider>
  );
}
